export interface City {
    code: number,
    codeDpt: number,
    codePst: number[],
    coord: number[],
    name: string,
    nameDpt: string,
}

export interface Weather {
    city: {
        code: number,
        codeDpt: number,
        codePst: number[],
        coord: number[],
        name: string,
        nameDpt: string,
    },
    data: {
        current_weather: {
            temperature: number,
            time: any,
            weathercode: number,
            winddirection: number,
            windspeed: number,
        },
        elevation: number,
        generationtime_ms:number,
        latitude: number,
        longitude: number,
        utc_offset_seconds: number,
    }
}

export const capitalize = (s: string) => {
    return (s && s[0].toUpperCase() + s.slice(1))
}

export const weatherCode = (code: number) => {
    if (code === 0)
        return ("data:image/png;base64,"); // clear sky
    else if (code >= 1 && code <= 3)
        return ("data:image/png;base64,"); // few clouds
    else if (code === 4)
        return ("data:image/png;base64,"); // smoke
    else if (code === 5)
        return ("data:image/png;base64,"); // haze
    else if ((code >= 6 && code <= 9) || (code >= 30 && code <= 35))
        return ("data:image/png;base64,"); // dust and sand
    else if (code >= 10 && code <= 12)
        return ("data:image/png;base64,"); // mist
    else if ((code >= 13 && code <= 19) || (code >= 94 && code <= 99))
        return ("data:image/png;base64,"); // lightning and thunder
    else if (code >= 20 && code <= 29)
        return ("data:image/png;base64,"); // precipitation, fog, ice fog or thunderstorm at the station during the preceding
    else if ((code >= 36 && code <= 39) || (code >= 70 && code <= 79) || (code >= 83 && code <= 93))
        return ("data:image/png;base64,"); // snow
    else if (code >= 40 && code <= 49)
        return ("data:image/png;base64,"); // fog
    else if (code >= 50 && code <= 59)
        return ("data:image/png;base64,"); // drizzle
    else if ((code >= 60 && code <= 69) || (code >= 80 && code <= 82))
        return ("data:image/png;base64,"); // rain
    return (undefined); // unknown
}

export const windDir = (dir : number) => {
    if (dir >= 0 && dir <= 11.25)
        return ("Nord");
    else if (dir >= 11.26 && dir <= 33.75)
        return ("Nord-Nord-Est");
    else if (dir >= 33.76 && dir <= 56.25)
        return ("Nord-Est");
    else if (dir >= 56.26 && dir <= 78.75)
        return ("Est-Nord-Est");
    else if (dir >= 78.76 && dir <= 101.25)
        return ("Est");
    else if (dir >= 101.26 && dir <= 123.75)
        return ("Est-Sud-Est");
    else if (dir >= 123.76 && dir <= 146.25)
        return ("Sud-Est");
    else if (dir >= 146.26 && dir <= 168.75)
        return ("Sud-Sud-Est");
    else if (dir >= 168.76 && dir <= 191.25)
        return ("Sud");
    else if (dir >= 191.26 && dir <= 213.75)
        return ("Sud-Sud-Ouest");
    else if (dir >= 213.76 && dir <= 236.25)
        return ("Sud-Ouest");
    else if (dir >= 236.26 && dir <= 258.75)
        return ("Ouest-Sud-Ouest");
    else if (dir >= 258.76 && dir <= 281.25)
        return ("Ouest");
    else if (dir >= 281.26 && dir <= 303.75)
        return ("Ouest-Nord-Ouest");
    else if (dir >= 303.76 && dir <= 326.25)
        return ("Nord-Ouest");
    else if (dir >= 326.26 && dir <= 348.75)
        return ("Nord-Nord-Ouest");
    else if (dir >= 348.76 && dir <= 360)
        return ("Nord");
}